Concert.create([
  { :date => "2013-08-25 16:00:00", :head => "Marielle et Katia Labèque, pianistes", :subhead => "Dimanche 25 août, à 17h. au château de Tannay", :description => "Les Variations Musicales de Tannay » accueillent en ouverture du Festival 2013 \n\tles « deux sœurs pianistes les plus célèbres de la scène classique internationale ». Formées au \n\tconservatoire de Paris, elles ont remporté de nombreux prix et  l’impressionnante variété de leur \n\trépertoire les fait jouer aux quatre coins de la planète : New York, Berlin, Vienne, Londres...ainsi\n\t que dans les orchestres les plus prestigieux sous la direction des plus grands.", :created_at => "2013-05-06 16:34:38", :updated_at => "2013-05-06 16:34:38", :playlist => ["Debussy : Nuages et fêtes", "Ravel : Rapsody espagnole", "Gershwin : trois préludes", "Philip Glass : quatre mouvements pour deux pianos"], :shortname => "Marielle et Katia Labèque", :price => 6000, :category => "concert" },
  { :date => "2013-08-28 19:00:00", :head => "David Greilsammer, en récital pour deux pianos", :subhead => "Mercredi 28 août, à 20h. au château de Tannay", :description => "Né à Jérusalem, sacré « révélation » aux Victoires de la  musique en 2008, \n\tDavid Greilsammer est reconnu comme l’un des artistes les plus audacieux et visionnaires de sa génération.\n\t En tant que pianiste, il s’est produit dans les plus grandes salles et a collaboré avec les plus grands \n\t orchestres. Il propose ce soir, à Tannay, et  en premier suisse romande, une performance pianistique \n\t passant en 65 minutes d’une sonate baroque à une sonate contemporaine et inversement.", :created_at => "2013-05-06 16:34:38", :updated_at => "2013-05-06 16:34:38", :playlist => ["15 sonates de Scarlatti et de Cage"], :shortname => "David Greilsammer", :price => 4000, :category => "concert" },
  { :date => "2013-08-30 19:00:00", :head => "Béatrice Berrut, pianiste, avec l’orchestre du festival sous la direction de Jonathan Haskell", :subhead => "Vendredi 30 août, à 20h. au château de Tannay", :description => "Beatrice Berrut est née en 1985 dans le canton du Valais. Elle a déjà derrière elle une \n\ttrès belle carrière internationale. Et son dernier album consacré à l’intégrale des sonates de Schumann \n\tdonne à entendre un souffle lyrique véritable et un grand soin apporté aux détails. Au début de cette \n\tannée 2013, on a pu l’entendre au Victoria Hall de Genève, invitée par les Amis de l'OSR. Gidon Kremer \n\tla décrit comme «  une artiste à la sensibilité et à l'engagement impressionnants »", :created_at => "2013-05-06 16:34:38", :updated_at => "2013-05-06 16:34:38", :playlist => ["Romance pour cordes op.42 de Sibelius", "Concerto no.27, en si bémol K 595 de Mozart", "Pelléas et Mélisande de G.Fauré", "Concerto pour piano en la mineur op.54 de R.Schumann"], :shortname => "Béatrice Berrut", :price => 5000, :category => "concert" },
  { :date => "2013-08-31 15:30:00", :head => "L’ensemble Intermezzo, sous la direction d’Ahmed Hamdy", :subhead => "Samedi 31 août, à 16h.30 au château de Tannay. Concert à l’intention des enfants", :description => "C’est en 1768, lors de son troisième voyage à Vienne en compagnie de son père Leopold, \n\tque Mozart, âgé de 12 ans seulement, compose l’opéra Bastien et Bastienne. Séduit par la charmante \n\thistoire d’amour de l’intermède Le Devin du village de Jean-Jacques Rousseau, Mozart l’adapta pour \n\tl’opéra et composa Bastien et Bastienne comme une pièce bucolique dans le goût champêtre de l’époque.", :created_at => "2013-05-06 16:34:38", :updated_at => "2013-05-06 16:34:38", :playlist => ["Bastien et Bastienne, de Mozart, avec un orchestre de musiciens de l’Orchestre de \n\t\tchambre de Genève et de l’ensemble da Capo, un chœur d’enfants ainsi qu’une récitante."], :shortname => "L’ensemble Intermezzo", :price => 0, :category => "concert" },
  { :date => "2013-09-01 16:00:00", :head => "Jean-Guihen Queyras, violoncelliste, avec l’Orchestre du Festival sous la direction de \n\tJonathan Haskell", :subhead => "Dimanche 1er septembre à 17h. au château de Tannay", :description => "Aussi à l’aise dans le répertoire baroque que contemporain, le violoncelliste \n\tfrançais Jean-Guihen Queyras (né en 1967) figure parmi les musiciens les plus marquants de sa \n\tgénération. Membre du quatuor à cordes Arcanto, il a pour partenaire privilégié de musique de \n\tchambre le pianiste Alexandre Tharaud que les Variations Musicales de Tannay recevait il y a \n\tdeux ans. Jean-Guihen est Professeur à la Musikhochschule de Stuttgart et co-directeur artistique \n\tdes Rencontres Musicales de Haute-Provence qui ont lieu chaque année au mois de juillet à Forcalquier. \n\tDepuis novembre 2005, il joue un violoncelle de Gioffredo Cappa de 1696.", :created_at => "2013-05-06 16:34:38", :updated_at => "2013-05-06 16:34:38", :playlist => ["Sérénade pour cordes d’Elgar", "Andante cantabile en ré majeur op.11 de Tchaïkovski", "Concerto  no.1 en ut majeur Hob.VII bl de Haydn", "Elégie de la Sérénade pour cordes de Tchaïkovski", "St Paul's Suite pour cordes de Holst", "Symphonie no.34 en do majeur K 238 de Mozart"], :shortname => "Jean-Guihen Queyras", :price => 5000, :category => "concert" },
  { :date => "2013-09-01 16:00:00", :head => "Abonnement 2013", :subhead => "Libre accès à tous les concerts de l'éditition 2013", :description => "Optez pour l'abonnement des Variations Musicales de Tannay pour avoir accès à l'entier de la programmation à tariff avantageux. Vous pouvez l'acheter à la billetterie.", :created_at => "2013-05-06 16:34:38", :updated_at => "2013-05-06 16:34:38", :playlist => [], :shortname => "Abonnement", :price => 20000, :category => "abonnement" },
  { :date => "2013-08-31 19:00:00", :head => "Nicolas Angelich, pianiste en récital", :subhead => "Samedi 31 août, à 20h. au château de Tannay", :description => "Né aux Etats Unis en 1970, Nicholas Angelich donne son premier concert à 7 ans et entre à 13 ans au Conservatoire National Supérieur de Paris où il étudie avec Aldo Ciccolini, et Michel Beroff. En 2003, il fait ses débuts avec le New York Philharmonic et Kurt Masur . On pourra ensuite l’entendre dans les plus grands festivals, notamment à Verbier ainsi qu’au Festival Martha Argerich de Lugano. Nicholas Angelich s’est produit ces dernières années avec les plus grands orchestres symphoniques, mais il est aussi passionné de musique de chambre, aux côtés, notamment, de Joshua Bell, Maxim Vengerov, Renaud et Gautier Capuçon. Il a été désigné cette année « meilleur soliste instrumental » aux « Victoires de la Musique Classique »", :playlist => ["Sonate op.10 no.1 de Beethoven", "Etudes et Mazurkas de Chopin", "Kreisleriana op.16 de Schumann"], :shortname => "Nicolas Angelich", :price => 5000, :category => "concert"}
